mapper.select:
select * from tbl_cms_topic
and id = #{id}
and name like concat('%',#{name}, '%')
and is_display = #{isDisplay,jdbcType=INTEGER}
and IF(start_time IS NOT NULL AND start_time!='', TIMESTAMP(start_time) > current_timestamp(),start_time IS NOT NULL AND start_time!='')
and IF(start_time IS NOT NULL and start_time!='' and end_time IS NOT NULL AND end_time!='', TIMESTAMP(end_time) > current_timestamp() and TIMESTAMP(start_time) < current_timestamp() ,end_time IS NOT NULL AND end_time!='')
and IF(start_time IS NOT NULL AND end_time!='', TIMESTAMP(end_time) < current_timestamp(),end_time IS NOT NULL AND end_time!='')
and (start_time IS NULL OR start_time='') and (end_time IS NULL OR end_time='')
and start_time > #{startTime}
and end_time < #{endTime}
ORDER BY update_time DESC ,sort_no ,is_display desc ,create_time DESC

mapper.update:
update tbl_cms_topic set is_display = #{isDisplay}
where id in
#{id}

mapper.select:
select count(*) from tbl_cms_topic where 1=1

	and id != #{id}

and name = #{name}
